import React from "react";
import './Suppression.css';
import ReactDOM from "react-dom";
import GlobalSearchComponent from "./GlobalSearchComponent";
import ReactTable from "react-table-6";  
import "react-table-6/react-table.css" 


class SuppresionList extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
      filteredData: [],
      columns: [],
      searchInput: ""
    };
  }

  componentDidMount() {
    this.getData();
    this.getColumns();
  }

  getColumns = () => {
    let columns = [
      {
        Header: "Sr. No.",
        accessor: "srno",
        sortable: false,
        show: true,
        displayValue: " Sr. No."
      },
      {
        Header: "Suppression List Name",
        accessor: "sln",
        sortable: false,
        show: true,
        displayValue: "Suppression List Name "
      },
      {
        Header: "Last Modified On",
        accessor: "lmo",
        sortable: false,
        show: true,
        displayValue: " Last Modified On"
      },
      {
        Header: "Last Modified By",
        accessor: "lmb",
        sortable: false,
        show: true,
        displayValue: " Last Modified By"
      },
      {
        Header: "Records",
        accessor: "records",
        sortable: false,
        show: true,
        displayValue: " Records "
      },
    ];
    this.setState({ columns });
  };

  getData = () => {
    let data = [
      { srno: 1, sln: "Already Contacted", lmo: "28/10/2020", lmb:"John", records:3},
      { srno: 2, sln: "Market Leads", lmo: "21/9/2020", lmb:"Mike", records:3},
      { srno: 3, sln: "Do not Contact", lmo: "2/4/2020", lmb:"Jack", records:3},
      { srno: 4, sln: "Already Contacted", lmo: "2/4/2020", lmb:"Jill", records:3},
      { srno: 5, sln: "Market Leads", lmo: "28/10/2020", lmb:"Mike", records:3},
      { srno: 6, sln: "Market Leads", lmo: "2/4/2020", lmb:"Jill", records:3},
      { srno: 7, sln: "Do not Contact", lmo: "21/9/2020", lmb:"Mike", records:3},
      { srno: 8, sln: "Already Contacted", lmo: "21/9/2020", lmb:"Jack", records:3},
      { srno: 9, sln: "Market Leads", lmo: "2/4/2020", lmb:"John", records:3},
      { srno: 10, sln: "Do not Contact", lmo: "28/10/2020", lmb:"Jack", records:3},
      { srno: 11, sln: "Do not Contact", lmo: "21/9/2020", lmb:"John", records:3},
      { srno: 12, sln: "Market Leads", lmo: "28/10/2020", lmb:"Jack", records:3},
      { srno: 13, sln: "Already Contacted", lmo: "21/9/2020", lmb:"Jill", records:3},
      { srno: 14, sln: "Market Leads", lmo: "28/10/2020", lmb:"John", records:3},
      { srno: 15, sln: "Already Contacted", lmo: "28/10/2020", lmb:"John", records:3}
    ];
    this.setState({ data, filteredData: data });
  };

  handleSetData = data => {
    console.log(data);
    this.setState({ filteredData: data });
  };

  render() {
    let { filteredData, columns } = this.state;
    return (
      <div className="Suppression">
      <div> 
            <GlobalSearchComponent data={this.state.data} handleSetData={this.handleSetData} />
      </div>
        
        <ReactTable
          data={filteredData}
          columns={columns}
          defaultPageSize={4}
          className="-striped -highlight"
        />
      </div>
    );
  }
}
export default SuppresionList
