import React ,{useState}from "react";
import { Input } from "semantic-ui-react";
import './Suppression.css';


function DropDownComponent(props) {
  const [searchInput, setSearchInput] = useState("");

const handleChange =(e)=>{
  setSearchInput({
    searchInput:e.target.value
  }, ()=> globalSearch())
}

const globalSearch = () => {
  let searchInput ="";
  let filteredData = props.data.filter(value => {
    return (
      value.sln.toLowerCase().includes(searchInput.toLowerCase()) ||
      value.lmb.toLowerCase().includes(searchInput.toLowerCase()) 
    );
  });
  props.handleSetData(filteredData);
};
  return (
    <>
        <br />
        <Input />
        <Input
          size="large"
          name="searchInput"
          value={searchInput}
          onChange={handleChange}
          placeholder="Search"
          className="Input"
        />
        <br />
        <br />
      </>
  )
}

export default DropDownComponent



